function palindrome(str) {
  // Good luck!
  var reverseStr = ''

  //removes all non-alphanumeric characters
  str = str.replace(/\W|_|,/g, "").toLowerCase()

  //reverses the string
  reverseStr = str.split("").reverse().join("")

  //checks for palindrome
  if (str === reverseStr) {
    return true;
  } else {
    return false;
  }
}



